import React, { useState } from 'react';

const BookingConfirmation = () => {
  const [showCalendarOptions, setShowCalendarOptions] = useState(false);

  const eventDetails = {
    title: 'Work.Life Bermondsey Booking',
    start: '2025-01-21T14:30:00',
    description: 'Thank you for booking with us!',
    location: 'Work.Life Bermondsey',
  };

  const addToCalendar = (calendarType) => {
    let url = '';

    switch (calendarType) {
      case 'Google':
        url = `https://calendar.google.com/calendar/render?action=TEMPLATE&text=${encodeURIComponent(
          eventDetails.title
        )}&dates=${eventDetails.start.replace(/[-:]/g, '')}/${eventDetails.start
          .replace(/[-:]/g, '')
          .slice(0, -2)}&details=${encodeURIComponent(
          eventDetails.description
        )}&location=${encodeURIComponent(eventDetails.location)}`;
        break;
      case 'Apple':
      case 'Outlook.com':
      case 'Office365':
      case 'Yahoo':
        alert(`The option to add to ${calendarType} is not implemented yet!`);
        break;
      default:
        console.error('Unknown calendar type');
    }

    if (url) {
      window.open(url, '_blank');
    }
  };

  return (
    <div className="booking-container">
      <h1>Thank you for your booking, see you soon!</h1>
      <div className="details">
        <p>Work.Life Bermondsey</p>
        <p>Tuesday 21 January</p>
        <p>02.30pm</p>
      </div>
      <button
        className="btn"
        onClick={() => setShowCalendarOptions(!showCalendarOptions)}
      >
        Add to calendar
      </button>
      {showCalendarOptions && (
        <div className="calendar-options">
          <button onClick={() => addToCalendar('Apple')}>Apple</button>
          <button onClick={() => addToCalendar('Google')}>Google</button>
          <button onClick={() => addToCalendar('Outlook.com')}>
            Outlook.com
          </button>
          <button onClick={() => addToCalendar('Office365')}>Office365</button>
          <button onClick={() => addToCalendar('Yahoo')}>Yahoo</button>
        </div>
      )}
      <p>Please let us know how you heard about us</p>
      <select className="dropdown">
        <option value="">Please Select</option>
        <option value="Google">Google</option>
        <option value="Friend">Friend</option>
        <option value="Advertisement">Advertisement</option>
        <option value="Social Media">Social Media</option>
      </select>
      <button className="submit-btn">Submit</button>
    </div>
  );
};

export default BookingConfirmation;
