import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import '../styles/BookTour.css';

const BookTour = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    location: '',
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    desks: '',
    startDate: '',
    interest: '',
  });
  const [selectedMonth, setSelectedMonth] = useState('January'); // For Step 2
  const [selectedDate, setSelectedDate] = useState(''); // For Step 2

  const navigate = useNavigate(); // Hook to handle navigation

  // Generate days dynamically for the selected month
  const generateDays = (month) => {
    const daysInMonth = new Date(2025, new Date(Date.parse(`${month} 1, 2025`)).getMonth() + 1, 0).getDate();
    const daysArray = [];

    for (let i = 1; i <= daysInMonth; i++) {
      const date = new Date(2025, new Date(Date.parse(`${month} 1, 2025`)).getMonth(), i);
      const day = date.toLocaleString('en-US', { weekday: 'short' }); // e.g., "Mon", "Tue"
      daysArray.push({ day, date: i });
    }

    return daysArray;
  };

  const days = generateDays(selectedMonth);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const nextStep = () => {
    if (step < 2) setStep(step + 1);
  };

  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', { ...formData, selectedMonth, selectedDate });
    navigate('/thank-you'); // Navigate to the Thank You page after submission
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>
      <div className="steps">
        <div className={`step ${step === 1 ? 'active' : ''}`}>
          <span>1</span>
        </div>
        <span className={`step-label ${step === 1 ? 'active' : ''}`}>About you</span>

        <div className={`step ${step === 2 ? 'active' : ''}`}>
          <span>2</span>
        </div>
        <span className={`step-label ${step === 2 ? 'active' : ''}`}>Date & time</span>
      </div>

      {step === 1 && (
        <form>
          {/* Form Step 1 */}
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select
              id="location"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              required
            >
              <option value="">Select location</option>
              <option value="London">London</option>
              <option value="New York">New York</option>
              <option value="Berlin">Berlin</option>
              <option value="Paris">Paris</option>
              <option value="Rome">Rome</option>
            </select>
          </div>

          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input
                type="text"
                name="firstName"
                placeholder="First name"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last name"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Work email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="number"
              name="desks"
              placeholder="How many desks do you need?"
              value={formData.desks}
              onChange={handleInputChange}
              required
            />
            <input
              type="date"
              name="startDate"
              placeholder="When do you want to start?"
              value={formData.startDate}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-buttons">
            <button type="button" onClick={nextStep} className="next-button">
              Agree and proceed →
            </button>
          </div>
        </form>
      )}

{step === 2 && (
        <form onSubmit={handleSubmit}>
          {/* Step 2 Implementation */}
          <div
            className="tourBookingForm bookingStep2Formfield"
            data-daystartday={dataAttributes.dayStartDay}
            data-dayendday={dataAttributes.dayEndDay}
            data-curdatecom={dataAttributes.curDateCom}
            data-lastmondayday2nd={dataAttributes.lastMondayDay2nd}
            data-day2nd={dataAttributes.day2nd}
            data-day3rd={dataAttributes.day3rd}
            data-lastmondayday3rd={dataAttributes.lastMondayDay3rd}
            data-curdatecom-flag={dataAttributes.curDateComFlag}
            data-curdateday={dataAttributes.curDateDay}
          >
            <div className="fieldGroupTitle">
              Select your date range: {dataAttributes.dayStartDay} to{' '}
              {dataAttributes.dayEndDay}
            </div>

            {/* Month Selection */}
            <div className="month-tabs">
              {['January', 'February', 'March'].map((month) => (
                <button
                  key={month}
                  className={selectedMonth === month ? 'active' : ''}
                  onClick={() => setSelectedMonth(month)}
                >
                  {month}
                </button>
              ))}
            </div>

            {/* Days Selection */}
            <div className="days-picker">
              {[...Array(31)].map((_, index) => {
                const day = index + 1;
                return (
                  <button
                    key={day}
                    className={selectedDate === day ? 'active' : ''}
                    onClick={() => setSelectedDate(day)}
                  >
                    {day}
                  </button>
                );
              })}
            </div>
          </div>

          <div className="form-buttons">
            <button type="button" onClick={prevStep} className="prev-button">
              ← Back
            </button>
            <button type="submit" className="submit-button">
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
