import React, { useState } from "react";
import "../styles/BookTour.css";

const BookTour = () => {
  const [step, setStep] = useState(2);
  const [selectedMonth, setSelectedMonth] = useState("January");
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [currentDayStart, setCurrentDayStart] = useState(0);

  const months = ["January", "February", "March"];
  const daysInMonth = {
    January: 31,
    February: 28,
    March: 31,
  };

  const timeSlots = [
    "9:00 am",
    "9:30 am",
    "10:00 am",
    "10:30 am",
    "11:00 am",
    "11:30 am",
    "12:00 pm",
    "12:30 pm",
    "1:00 pm",
    "1:30 pm",
    "2:00 pm",
    "2:30 pm",
    "3:00 pm",
    "3:30 pm",
    "4:00 pm",
    "4:30 pm",
    "5:00 pm",
  ];

  const handleMonthChange = (month) => {
    setSelectedMonth(month);
    setSelectedDate(null);
    setCurrentDayStart(0); // Reset the visible days to start from the beginning
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const handleDayScroll = (direction) => {
    const maxDays = daysInMonth[selectedMonth];
    if (direction === "prev") {
      setCurrentDayStart((prev) => Math.max(0, prev - 7));
    } else {
      setCurrentDayStart((prev) => Math.min(maxDays - 7, prev + 7));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedDate || !selectedTime) {
      alert("Please select both a date and time.");
      return;
    }
    alert(`Booking confirmed for ${selectedMonth} ${selectedDate} at ${selectedTime}`);
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>
      <div className="steps">
        <div className={`step ${step === 1 ? "active" : ""}`}>
          <span>1</span>
          <span className="step-label">About you</span>
        </div>
        <div className={`step ${step === 2 ? "active" : ""}`}>
          <span>2</span>
          <span className="step-label">Date & time</span>
        </div>
      </div>

      {/* Step 2: Date & Time */}
      {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="date-label">
              Pick a date*
            </label>
            <div className="month-tabs">
              {months.map((month) => (
                <button
                  key={month}
                  className={`month-tab ${selectedMonth === month ? "active" : ""}`}
                  onClick={() => handleMonthChange(month)}
                  type="button"
                >
                  {month}
                </button>
              ))}
            </div>
            <div className="date-selector">
              <button
                className="arrow-button"
                onClick={() => handleDayScroll("prev")}
                disabled={currentDayStart === 0}
              >
                &lt;
              </button>
              {Array.from({ length: 7 }, (_, index) => currentDayStart + index + 1)
                .filter((day) => day <= daysInMonth[selectedMonth])
                .map((day) => {
                  const currentDate = new Date();
                  const selectedDateObject = new Date(
                    `2025-${months.indexOf(selectedMonth) + 1}-${day}`
                  );

                  // Disable past dates
                  const isPastDate = selectedDateObject < currentDate.setHours(0, 0, 0, 0);

                  // Disable weekends
                  const isWeekend =
                    selectedDateObject.getDay() === 0 || selectedDateObject.getDay() === 6;

                  return (
                    <button
                      key={day}
                      className={`date-button ${selectedDate === day ? "active" : ""}`}
                      onClick={() => {
                        if (!isPastDate && !isWeekend) handleDateChange(day);
                      }}
                      type="button"
                      disabled={isPastDate || isWeekend}
                      style={{
                        opacity: isPastDate || isWeekend ? 0.5 : 1,
                        cursor: isPastDate || isWeekend ? "not-allowed" : "pointer",
                      }}
                    >
                      {day}
                    </button>
                  );
                })}
              <button
                className="arrow-button"
                onClick={() => handleDayScroll("next")}
                disabled={currentDayStart + 7 >= daysInMonth[selectedMonth]}
              >
                &gt;
              </button>
            </div>
          </div>

          {/* Time Slots */}
          <div className="form-group">
            <label className="time-label">What time?*</label>
            {selectedDate && (
              <div className="time-slots">
                {timeSlots.map((time) => (
                  <button
                    key={time}
                    className={`time-slot ${selectedTime === time ? "active" : ""}`}
                    onClick={() => handleTimeChange(time)}
                    type="button"
                  >
                    {time}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Guide Info */}
          <div className="guide-info">
            <img
              src="https://via.placeholder.com/50"
              alt="Niamh"
              className="guide-image"
            />
            <p>Niamh will be there to meet you</p>
          </div>

          {/* Buttons */}
          <div className="form-buttons">
            <button type="button" onClick={() => setStep(1)} className="back-button">
              ‚Üê Back
            </button>
            <button type="submit" className="submit-button">
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
