import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import AddToCalendar from 'react-add-to-calendar';

const BookingConfirmation = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Get booking details from the state passed during navigation
  const bookingDetails = location.state?.bookingDetails;

  if (!bookingDetails) {
    // Redirect back to the form if no details are available
    navigate('/book-confirmation');
    return null;
  }

  // Prepare event details for AddToCalendar
  const event = {
    title: 'Booking Confirmation',
    description: `Thank you for your booking at ${bookingDetails.location}. See you soon!`,
    location: bookingDetails.location,
    startTime: new Date(`${bookingDetails.startDate} ${bookingDetails.time}`).toISOString(),
    endTime: new Date(
      new Date(`${bookingDetails.startDate} ${bookingDetails.time}`).getTime() + 60 * 60 * 1000 // 1-hour duration
    ).toISOString(),
  };

  // Function to handle email confirmation
  const handleSubmit = async () => {
    const { location: eventLocation, startDate, time, email } = bookingDetails;

    const emailData = {
      to: email,
      subject: 'Booking Confirmation',
      body: `
        Dear Customer,
        
        Thank you for your booking at ${eventLocation}.
        
        Your booking details are as follows:
        - Date: ${new Date(startDate).toLocaleDateString('en-US', {
          weekday: 'long',
          day: 'numeric',
          month: 'long',
        })}
        - Time: ${time}
        
        We look forward to seeing you!
        
        Best regards,
        Your Company
      `,
    };

    try {
      const response = await fetch('https://your-backend-endpoint.com/send-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(emailData),
      });

      if (response.ok) {
        alert('Email confirmation sent!');
        navigate('/'); // Redirect to the homepage
      } else {
        alert('Failed to send email confirmation.');
      }
    } catch (error) {
      console.error('Error sending email:', error);
      alert('An error occurred while sending the email.');
    }
  };

  return (
    <div className="confirmation-container">
      <h2>Thank you for your booking, see you soon!</h2>
      <div className="booking-details">
        <h3>{bookingDetails.location}</h3>
        <p>
          {new Date(bookingDetails.startDate).toLocaleDateString('en-US', {
            weekday: 'long',
            day: 'numeric',
            month: 'long',
          })}
        </p>
        <p>{bookingDetails.time}</p>
      </div>

      {/* Add to Calendar Dropdown */}
      <AddToCalendar event={event} buttonLabel="Add to calendar" />

      <div className="feedback-section">
        <h3>Please let us know how you heard about us</h3>
        <select className="feedback-dropdown">
          <option value="">Please Select</option>
          <option value="Google Search">Google Search</option>
          <option value="Social Media">Social Media</option>
          <option value="Friend Referral">Friend Referral</option>
          <option value="Other">Other</option>
        </select>
        <button className="feedback-submit" onClick={handleSubmit}>
          Submit
        </button>
      </div>
    </div>
  );
};

export default BookingConfirmation;
