import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/BookTour.css';

const BookTour = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    location: '',
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    desks: '',
    startDate: '',
    interest: '',
  });

  const [selectedMonth, setSelectedMonth] = useState('January');
  const [selectedDate, setSelectedDate] = useState('');
  const navigate = useNavigate();

  // Example Data Attributes (set dynamically if required)
  const [dataAttributes] = useState({
    dayStartDay: '2025-01-20',
    dayEndDay: '2025-03-31',
    curDateCom: '20250120',
    lastMondayDay2nd: '2025-02-03',
    day2nd: '20250203',
    day3rd: '20250303',
    lastMondayDay3rd: '2025-03-03',
    curDateComFlag: '1',
    curDateDay: '2025-01-20',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const nextStep = () => {
    if (step < 2) setStep(step + 1);
  };

  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', { ...formData, selectedMonth, selectedDate });
    navigate('/thank-you');
  };

  // Get current date to check for past dates
  const currentDate = new Date();
  const formattedCurrentDate = currentDate.toISOString().split('T')[0]; // Format as YYYY-MM-DD

  // Function to check if the selected date is in the past
  const isPastDate = (selectedYear, selectedMonth, selectedDay) => {
    const selectedDateObj = new Date(selectedYear, selectedMonth - 1, selectedDay);
    return selectedDateObj < currentDate;
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>
      <div className="steps">
        <div className={`step ${step === 1 ? 'active' : ''}`}>
          <span>1</span>
        </div>
        <span className={`step-label ${step === 1 ? 'active' : ''}`}>About you</span>

        <div className={`step ${step === 2 ? 'active' : ''}`}>
          <span>2</span>
        </div>
        <span className={`step-label ${step === 2 ? 'active' : ''}`}>Date & time</span>
      </div>

      {step === 1 && (
        <form>
          {/* Form Step 1 */}
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select
              id="location"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              required
            >
              <option value="">Select location</option>
              <option value="London">London</option>
              <option value="New York">New York</option>
              <option value="Berlin">Berlin</option>
              <option value="Paris">Paris</option>
              <option value="Rome">Rome</option>
            </select>
          </div>

          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input
                type="text"
                name="firstName"
                placeholder="First name"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last name"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Work email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="number"
              name="desks"
              placeholder="How many desks do you need?"
              value={formData.desks}
              onChange={handleInputChange}
              required
            />
            <input
              type="date"
              name="startDate"
              placeholder="When do you want to start?"
              value={formData.startDate}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-buttons">
            <button type="button" onClick={nextStep} className="next-button">
              Agree and proceed →
            </button>
          </div>
        </form>
      )}

      {step === 2 && (
        <form onSubmit={handleSubmit}>
          {/* Date Selection Section */}
          <div
            className="tourBookingForm bookingStep2Formfield"
            data-daystartday={dataAttributes.dayStartDay}
            data-dayendday={dataAttributes.dayEndDay}
          >
            <div className="fieldGroupTitle">
              Pick a date: {dataAttributes.dayStartDay} to{' '}
              {dataAttributes.dayEndDay}
            </div>

            {/* Month Selection */}
            <div className="month-tabs">
              {['January', 'February', 'March'].map((month) => (
                <button
                  key={month}
                  type="button"
                  className={`month-tab ${selectedMonth === month ? 'active' : ''}`}
                  onClick={() => setSelectedMonth(month)}
                >
                  {month}
                </button>
              ))}
            </div>

            {/* Days Selection */}
            <div className="days-picker">
              {[...Array(31)].map((_, index) => {
                const day = index + 1;
                const isUnavailable =
                  selectedMonth === 'January' && day === 25; // Example unavailable date
                const isPast = isPastDate(2025, 1, day); // Check for past dates in January
                return (
                  <button
                    key={day}
                    type="button"
                    className={`day-button ${
                      selectedDate === day ? 'active' : ''
                    } ${isUnavailable || isPast ? 'unavailable' : ''}`}
                    onClick={() => {
                      if (!(isUnavailable || isPast)) setSelectedDate(day);
                    }}
                    disabled={isUnavailable || isPast}
                  >
                    {day}
                  </button>
                );
              })}
            </div>

            {/* Error Message for Unavailable Date */}
            {selectedMonth === 'January' && selectedDate === 25 && (
              <div className="error-message">
                Sorry! We're not available then, please select another date or call
                our team on 020 8124 3416.
              </div>
            )}
            {/* Error Message for Past Date */}
            {selectedDate && isPastDate(2025, 1, selectedDate) && (
              <div className="error-message">
                Sorry! The selected date is in the past. Please choose another date.
              </div>
            )}

            {/* Time Slots */}
            {selectedDate && (
              <>
                <div className="fieldGroupTitle">What Time?:</div>
                <div className="time-slots">
                  {['09:30 AM', '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM', '12:00 PM', '01:30 PM', '02:00 PM', '02:30 PM', '03:00 PM', '05:00 PM'].map(
                    (time) => (
                      <button
                        key={time}
                        type="button"
                        className={`time-slot ${
                          formData.time === time ? 'active' : ''
                        }`}
                        onClick={() => setFormData({ ...formData, time })}
                      >
                        {time}
                      </button>
                    )
                  )}
                </div>
              </>
            )}
          </div>

          <div className="form-buttons">
            <button type="button" onClick={prevStep} className="prev-button">
              ← Back
            </button>
            <button
              type="submit"
              className="submit-button"
              disabled={!selectedDate || !formData.time}
            >
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
