import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/BookTour.css"; // Custom CSS for styling

const BookTour = () => {
  const [step, setStep] = useState(1); // Tracks the current step
  const [formData, setFormData] = useState({
    location: "",
    firstName: "",
    lastName: "",
    phone: "",
    email: "",
    desks: "",
    interest: "",
  });
  const [currentMonthIndex, setCurrentMonthIndex] = useState(0); // Tracks the current month index
  const [visibleDates, setVisibleDates] = useState([]); // Dates for the current month
  const [selectedDate, setSelectedDate] = useState(null); // Selected date
  const [selectedTime, setSelectedTime] = useState(null); // Selected time
  const navigate = useNavigate();

  const months = ["January", "February", "March"]; // Supported months
  const timeSlots = [
    "09:30 AM",
    "10:00 AM",
    "10:30 AM",
    "11:00 AM",
    "11:30 AM",
    "12:00 PM",
    "01:30 PM",
    "02:00 PM",
    "02:30 PM",
    "03:00 PM",
    "05:00 PM",
  ];

  // Generate days dynamically for the current month
  const getDaysInMonth = (month, year) => {
    return new Array(new Date(year, month + 1, 0).getDate()).fill(null).map((_, i) => i + 1);
  };

  // Update visible dates based on the current month index
  useEffect(() => {
    setVisibleDates(getDaysInMonth(currentMonthIndex, 2025)); // Assuming year 2025
  }, [currentMonthIndex]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleMonthChange = (direction) => {
    if (direction === "prev" && currentMonthIndex > 0) {
      setCurrentMonthIndex(currentMonthIndex - 1);
      setSelectedDate(null); // Reset date when switching months
    } else if (direction === "next" && currentMonthIndex < months.length - 1) {
      setCurrentMonthIndex(currentMonthIndex + 1);
      setSelectedDate(null); // Reset date when switching months
    }
  };

  const handleDateChange = (day) => {
    setSelectedDate(day);
    setSelectedTime(null); // Reset time when the date changes
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedDate || !selectedTime) {
      alert("Please select both a date and time.");
      return;
    }
    const bookingDetails = {
      ...formData,
      startDate: `${months[currentMonthIndex]} ${selectedDate}`,
      time: selectedTime,
    };
    navigate("/book-confirmation", { state: { bookingDetails } });
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>

      {/* Step Indicators */}
      <div className="steps">
        <div className={`step ${step === 1 ? "active" : ""}`}>
          <span>1</span>
          <p>About you</p>
        </div>
        <div className={`step ${step === 2 ? "active" : ""}`}>
          <span>2</span>
          <p>Date & time</p>
        </div>
      </div>

      {/* Step 1: About You */}
      {step === 1 && (
        <form>
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select
              id="location"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              required
            >
              <option value="">Select location</option>
              <option value="Bermondsey">Bermondsey</option>
              <option value="Soho">Soho</option>
              <option value="Camden">Camden</option>
            </select>
          </div>

          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input
                type="text"
                name="firstName"
                placeholder="First name"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last name"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Work email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="number"
              name="desks"
              placeholder="How many desks do you need?"
              value={formData.desks}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>What are you interested in?*</label>
            <div className="interest-options">
              <button
                type="button"
                className={formData.interest === "Private Office" ? "selected" : ""}
                onClick={() => setFormData({ ...formData, interest: "Private Office" })}
              >
                Private office
              </button>
              <button
                type="button"
                className={formData.interest === "Coworking" ? "selected" : ""}
                onClick={() => setFormData({ ...formData, interest: "Coworking" })}
              >
                Coworking
              </button>
            </div>
          </div>

          <div className="form-buttons">
            <button type="button" onClick={() => setStep(2)} className="next-button">
              Agree and proceed →
            </button>
          </div>
        </form>
      )}

      {/* Step 2: Date & Time */}
      {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="form-group dateWrapper">
            <label className="date-label">
              Pick a date* <span className="date-range">(2025-01-20 to 2025-03-31)</span>
            </label>
            {/* Month Navigation */}
            <div className="date-navigation">
              <button
                onClick={() => handleMonthChange("prev")}
                disabled={currentMonthIndex === 0}
              >
                &lt;
              </button>
              <span>{months[currentMonthIndex]}</span>
              <button
                onClick={() => handleMonthChange("next")}
                disabled={currentMonthIndex === months.length - 1}
              >
                &gt;
              </button>
            </div>
            {/* Date Selector */}
            <div className="date-selector">
              {visibleDates.map((day) => {
                const today = new Date().setHours(0, 0, 0, 0);
                const selectedDateObject = new Date(2025, currentMonthIndex, day).getTime();

                const isWeekend =
                  new Date(2025, currentMonthIndex, day).getDay() % 6 === 0; // Disable weekends
                const isPastDate = selectedDateObject < today; // Disable past dates

                return (
                  <button
                    key={day}
                    className={`date-button ${selectedDate === day ? "active" : ""}`}
                    disabled={isWeekend || isPastDate}
                    onClick={() => handleDateChange(day)}
                  >
                    {day}
                  </button>
                );
              })}
            </div>
          </div>

          {/* Time Slots */}
          <div className="form-group">
            <label className="time-label">What time?*</label>
            {selectedDate && (
              <div className="time-slots">
                {timeSlots.map((time) => (
                  <button
                    key={time}
                    className={`time-slot ${selectedTime === time ? "active" : ""}`}
                    onClick={() => handleTimeChange(time)}
                  >
                    {time}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Buttons */}
          <div className="form-buttons">
            <button type="button" onClick={() => setStep(1)} className="back-button">
              ← Back
            </button>
            <button type="submit" className="submit-button">
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
