import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const BookingConfirmation = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Get booking details from the state passed during navigation
  const bookingDetails = location.state?.bookingDetails;

  if (!bookingDetails) {
    // Redirect back to the form if no details are available
    navigate('/book-confirmation');
    return null;
  }

 // Function to generate and download an iCalendar file (.ics)
 const generateICS = () => {
    const { location: eventLocation, startDate, time } = bookingDetails;

    // Format start and end time
    const startDateTime = new Date(`${startDate} ${time}`);
    const endDateTime = new Date(startDateTime);
    endDateTime.setHours(endDateTime.getHours() + 1); // Assume 1-hour duration

    // Create iCalendar format content
    const icsContent = `
BEGIN:VCALENDAR
VERSION:2.0
PRODID:-//Your Company//Booking System//EN
BEGIN:VEVENT
UID:${new Date().getTime()}@yourdomain.com
DTSTAMP:${new Date().toISOString().replace(/[-:]/g, "").split(".")[0]}Z
DTSTART:${startDateTime.toISOString().replace(/[-:]/g, "").split(".")[0]}Z
DTEND:${endDateTime.toISOString().replace(/[-:]/g, "").split(".")[0]}Z
SUMMARY:Booking at ${eventLocation}
DESCRIPTION:Thank you for booking with us. See you soon!
LOCATION:${eventLocation}
END:VEVENT
END:VCALENDAR
    `.trim();

    // Trigger file download
    const blob = new Blob([icsContent], { type: "text/calendar" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "booking.ics";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Function to open Google Calendar event creation
  const addToGoogleCalendar = () => {
    const { location: eventLocation, startDate, time } = bookingDetails;

    const startDateTime = new Date(`${startDate} ${time}`);
    const endDateTime = new Date(startDateTime);
    endDateTime.setHours(endDateTime.getHours() + 1); // Assume 1-hour duration

    const googleCalendarUrl = `https://calendar.google.com/calendar/r/eventedit?text=${encodeURIComponent(
      `Booking at ${eventLocation}`
    )}&dates=${startDateTime
      .toISOString()
      .replace(/[-:]/g, "")
      .split(".")[0]}Z/${endDateTime
      .toISOString()
      .replace(/[-:]/g, "")
      .split(".")[0]}Z&details=${encodeURIComponent(
      "Thank you for booking with us. See you soon!"
    )}&location=${encodeURIComponent(eventLocation)}`;

    window.open(googleCalendarUrl, "_blank");
  };

  return (
    <div className="confirmation-container">
      <h2>Thank you for your booking, see you soon!</h2>
      <div className="booking-details">
        <h3>{bookingDetails.location}</h3>
        <p>
          {new Date(bookingDetails.startDate).toLocaleDateString('en-US', {
            weekday: 'long',
            day: 'numeric',
            month: 'long',
          })}
        </p>
        <p>{bookingDetails.time}</p>
      </div>
      <button className="add-to-calendar" onClick={() => alert('Add to calendar functionality')}>
        Add to calendar
      </button>

      <div className="feedback-section">
        <h3>Please let us know how you heard about us</h3>
        <select className="feedback-dropdown">
          <option value="">Please Select</option>
          <option value="Google Search">Google Search</option>
          <option value="Social Media">Social Media</option>
          <option value="Friend Referral">Friend Referral</option>
          <option value="Other">Other</option>
        </select>
        <button className="feedback-submit" onClick={() => alert('Feedback submitted!')}>
          Submit
        </button>
      </div>
    </div>
  );
};

export default BookingConfirmation;
