import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import '..styles./Contact.css';
import 'leaflet/dist/leaflet.css'; // Leaflet map styling

const Contact = () => {
  const offices = [
    {
      name: 'London Office',
      position: [51.5074, -0.1278],
      address: '123 London St, London, UK',
      phone: '+44 20 1234 5678',
      email: 'london@work.life',
    },
    {
      name: 'New York Office',
      position: [40.7128, -74.0060],
      address: '456 NY Ave, New York, USA',
      phone: '+1 212 123 4567',
      email: 'ny@work.life',
    },
  ];

  return (
    <div className="contact">
      {/* Header Section */}
      <header className="contact-header">
        <h1>Contact Us</h1>
        <p>We'd love to hear from you. Reach out to us using the form or find us at one of our locations below.</p>
      </header>

      {/* Contact Form */}
      <section className="contact-form-section">
        <form className="contact-form">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" placeholder="Your Name" required />

          <label htmlFor="email">Email</label>
          <input type="email" id="email" name="email" placeholder="Your Email" required />

          <label htmlFor="phone">Phone</label>
          <input type="tel" id="phone" name="phone" placeholder="Your Phone" />

          <label htmlFor="message">Message</label>
          <textarea id="message" name="message" placeholder="Your Message" rows="5" required></textarea>

          <button type="submit" className="contact-submit">Submit</button>
        </form>
      </section>

      {/* Contact Information */}
      <section className="contact-info-section">
        <h2>Our Offices</h2>
        <div className="contact-info">
          {offices.map((office, index) => (
            <div className="office" key={index}>
              <h3>{office.name}</h3>
              <p>{office.address}</p>
              <p>Phone: {office.phone}</p>
              <p>Email: <a href={`mailto:${office.email}`}>{office.email}</a></p>
            </div>
          ))}
        </div>
      </section>

      {/* Map Section */}
      <section className="contact-map-section">
        <MapContainer center={[51.5074, -0.1278]} zoom={5} scrollWheelZoom={false} style={{ height: '400px', width: '100%' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          {offices.map((office, index) => (
            <Marker position={office.position} key={index}>
              <Popup>
                <strong>{office.name}</strong>
                <br />
                {office.address}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </section>
    </div>
  );
};

export default Contact;
