import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/BookTour.css"; // Include your custom CSS

const BookTour = () => {
  const [step, setStep] = useState(1); // Step tracker
  const [formData, setFormData] = useState({
    location: "",
    firstName: "",
    lastName: "",
    phone: "",
    email: "",
    desks: "",
    interest: "",
  });

  const [currentMonth, setCurrentMonth] = useState(0); // Tracks the current month index
  const [visibleDates, setVisibleDates] = useState([]); // Dates for the current month
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const navigate = useNavigate();

  const months = ["January", "February", "March"];
  const timeSlots = [
    "09:30 AM",
    "10:00 AM",
    "10:30 AM",
    "11:00 AM",
    "11:30 AM",
    "12:00 PM",
    "01:30 PM",
    "02:00 PM",
    "02:30 PM",
    "03:00 PM",
    "05:00 PM",
  ];

  // Generate days in a month dynamically (Handles February leap years)
  const getDaysInMonth = (month, year) => {
    return new Array(new Date(year, month + 1, 0).getDate()).fill(null).map((_, i) => i + 1);
  };

  // Update visible dates when the month changes
  useEffect(() => {
    setVisibleDates(getDaysInMonth(currentMonth, 2025)); // Assuming 2025 is the year
  }, [currentMonth]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleMonthChange = (direction) => {
    if (direction === "previous" && currentMonth > 0) {
      setCurrentMonth(currentMonth - 1);
      setSelectedDate(null); // Reset selected date when switching months
    } else if (direction === "next" && currentMonth < months.length - 1) {
      setCurrentMonth(currentMonth + 1);
      setSelectedDate(null); // Reset selected date when switching months
    }
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedTime(null); // Reset time when the date changes
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedDate || !selectedTime) {
      alert("Please select both a date and time.");
      return;
    }
    const bookingDetails = {
      ...formData,
      startDate: `${months[currentMonth]} ${selectedDate}`,
      time: selectedTime,
    };
    navigate("/book-confirmation", { state: { bookingDetails } });
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>

      {/* Step Indicators */}
      <div className="steps">
        <div className={`step ${step === 1 ? "active" : ""}`}>
          <span>1</span>
          <p>About you</p>
        </div>
        <div className={`step ${step === 2 ? "active" : ""}`}>
          <span>2</span>
          <p>Date & time</p>
        </div>
      </div>

      {/* Step 2: Date & Time */}
      {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="date-label">
              Pick a date* <span className="date-range">(2025-01-20 to 2025-03-31)</span>
            </label>
            {/* Month Navigation */}
            <div className="date-navigation">
              <button
                onClick={() => handleMonthChange("previous")}
                disabled={currentMonth === 0}
              >
                &lt;
              </button>
              <span>{months[currentMonth]}</span>
              <button
                onClick={() => handleMonthChange("next")}
                disabled={currentMonth === months.length - 1}
              >
                &gt;
              </button>
            </div>
            {/* Date Selector */}
            <div className="date-selector">
              {visibleDates.map((day) => {
                const isWeekend = new Date(2025, currentMonth, day).getDay() % 6 === 0; // Disable weekends
                const today = new Date().setHours(0, 0, 0, 0); // Today's date
                const selectedDateObject = new Date(2025, currentMonth, day).getTime();
                const isPastDate = selectedDateObject < today; // Disable past dates

                return (
                  <button
                    key={day}
                    className={`date-button ${selectedDate === day ? "active" : ""}`}
                    disabled={isWeekend || isPastDate}
                    onClick={() => handleDateChange(day)}
                  >
                    {day}
                  </button>
                );
              })}
            </div>
          </div>

          {/* Time Slots */}
          <div className="form-group">
            <label className="time-label">What time?*</label>
            {selectedDate && (
              <div className="time-slots">
                {timeSlots.map((time) => (
                  <button
                    key={time}
                    className={`time-slot ${selectedTime === time ? "active" : ""}`}
                    onClick={() => handleTimeChange(time)}
                  >
                    {time}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="guide-info">
            <img
              src="https://via.placeholder.com/50"
              alt="Niamh"
              className="guide-image"
            />
            <p>Niamh will be there to meet you</p>
          </div>

          {/* Buttons */}
          <div className="form-buttons">
            <button type="button" onClick={() => setStep(1)} className="back-button">
              ‚Üê Back
            </button>
            <button type="submit" className="submit-button">
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
