import React, { useState } from 'react';
;
import '../styles/BookTour.css'; // Custom CSS for styling

const BookTour = () => {
  const [step, setStep] = useState(1);
  const [selectedMonth, setSelectedMonth] = useState("January");
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);

  const months = ["January", "February", "March"];
  const dates = Array.from({ length: 31 }, (_, i) => i + 1); // 1 to 31
  const timeSlots = [
    "9:00 am",
    "9:30 am",
    "10:00 am",
    "10:30 am",
    "11:00 am",
    "11:30 am",
    "12:00 pm",
    "12:30 pm",
    "1:00 pm",
    "1:30 pm",
    "2:00 pm",
    "2:30 pm",
    "3:00 pm",
    "3:30 pm",
    "4:00 pm",
    "4:30 pm",
    "5:00 pm",
  ];

  const handleMonthChange = (month) => {
    setSelectedMonth(month);
    setSelectedDate(null); // Reset date
    setSelectedTime(null); // Reset time
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedTime(null); // Reset time when date changes
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedDate || !selectedTime) {
      alert("Please select both a date and time.");
      return;
    }
    alert(`Booking confirmed for ${selectedMonth} ${selectedDate} at ${selectedTime}`);
  };

  const prevStep = () => setStep((prev) => Math.max(1, prev - 1));
  const nextStep = () => setStep((prev) => Math.min(2, prev + 1));

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>
      <div className="steps">
        <div className={`step ${step === 1 ? "active" : ""}`}>
          <span>1</span>
          <span className="step-label">About you</span>
        </div>
        <div className={`step ${step === 2 ? "active" : ""}`}>
          <span>2</span>
          <span className="step-label">Date & time</span>
        </div>
      </div>

      {/* Step 1: About You */}
      {step === 1 && (
        <form>
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select id="location" required>
              <option value="">Select location</option>
              <option value="London">London</option>
              <option value="Berlin">Berlin</option>
              <option value="Paris">Paris</option>
            </select>
          </div>
          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input type="text" placeholder="First name" required />
              <input type="text" placeholder="Last name" required />
            </div>
            <input type="tel" placeholder="Phone" required />
            <input type="email" placeholder="Work email" required />
            <input type="number" placeholder="How many desks do you need?" required />
            <input type="date" placeholder="When do you want to start?" required />
          </div>
          <div className="form-group">
            <label>What are you interested in?*</label>
            <div className="interest-options">
              <button type="button">Private Office</button>
              <button type="button">Coworking</button>
            </div>
          </div>
          <div className="form-buttons">
            <button type="button" onClick={nextStep}>
              Agree and proceed →
            </button>
          </div>
        </form>
      )}

      {/* Step 2: Date & Time */}
      {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="date-label">
              Pick a date* <span className="date-range">(2025-01-20 to 2025-03-31)</span>
            </label>
            <div className="month-tabs">
              {months.map((month) => (
                <button
                  key={month}
                  className={`month-tab ${selectedMonth === month ? "active" : ""}`}
                  onClick={() => handleMonthChange(month)}
                  type="button"
                >
                  {month}
                </button>
              ))}
            </div>
            <div className="date-selector">
              {dates.map((date) => {
                const currentDate = new Date();
                const selectedDateObject = new Date(`${selectedMonth} ${date}, 2025`);

                // Disable weekends (Saturday and Sunday)
                const isWeekend =
                  selectedDateObject.getDay() === 6 || selectedDateObject.getDay() === 0;

                // Disable past dates
                const isPastDate = selectedDateObject < currentDate.setHours(0, 0, 0, 0);

                return (
                  <button
                    key={date}
                    className={`date-button ${selectedDate === date ? "active" : ""}`}
                    onClick={() => {
                      if (!isPastDate && !isWeekend) handleDateChange(date);
                    }}
                    type="button"
                    disabled={isPastDate || isWeekend}
                    style={{
                      opacity: isPastDate || isWeekend ? 0.5 : 1,
                      cursor: isPastDate || isWeekend ? "not-allowed" : "pointer",
                    }}
                  >
                    {date}
                  </button>
                );
              })}
            </div>
          </div>

          {/* Time Slots */}
          <div className="form-group">
            <label className="time-label">What time?*</label>
            {selectedDate && (
              <div className="time-slots">
                {timeSlots.map((time) => (
                  <button
                    key={time}
                    className={`time-slot ${selectedTime === time ? "active" : ""}`}
                    onClick={() => handleTimeChange(time)}
                    type="button"
                  >
                    {time}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="guide-info">
            <img
              src="https://via.placeholder.com/50"
              alt="Niamh"
              className="guide-image"
            />
            <p>Niamh will be there to meet you</p>
          </div>

          {/* Buttons */}
          <div className="form-buttons">
            <button type="button" onClick={prevStep} className="back-button">
              ← Back
            </button>
            <button type="submit" className="submit-button">
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
