import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { atcb_action } from 'add-to-calendar-button';
import '../styles/BookingConfirmation.css';

const BookingConfirmation = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [showCalendarOptions, setShowCalendarOptions] = useState(false);

  // Get booking details from the state passed during navigation
  const bookingDetails = location.state?.bookingDetails;

  if (!bookingDetails) {
    navigate('/book-confirmation');
    return null;
  }

  // Ensure date defaults to current date if it's missing
  const currentDate = new Date();
  const startDate = bookingDetails.startDate
    ? new Date(bookingDetails.startDate)
    : currentDate;

  // Handle email notification submission
  const handleSubmit = async () => {
    const emailData = {
      name: bookingDetails.name,
      date: startDate.toISOString().split('T')[0],
      time: bookingDetails.time || `${currentDate.getHours()}:${currentDate.getMinutes()}`,
      location: bookingDetails.location,
      email: bookingDetails.email,
    };

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/send-mail`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(emailData),
      });

      const responseText = await response.text();
      console.log('Response Text:', responseText);

      if (response.ok) {
        alert('Email confirmation sent with a calendar invite!');
      } else {
        console.error('Server Error:', response.status, responseText);
        alert('Failed to send email confirmation.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while sending the email.');
    }
  };

  // Handle "Add to Calendar" functionality
  const handleAddToCalendar = (calendarType) => {
    const { location: eventLocation, time } = bookingDetails;

    const startDateTime = new Date(`${startDate.toISOString().split('T')[0]} ${time || '09:00'}`).toISOString();
    const endDateTime = new Date(new Date(startDateTime).getTime() + 30 * 60 * 1000).toISOString();

    atcb_action({
      name: 'Tour confirmed for Work.Life',
      description: `Thank you for your booking at ${eventLocation}. See you soon!`,
      startDate: startDateTime.split('T')[0],
      endDate: endDateTime.split('T')[0],
      startTime: startDateTime.split('T')[1].split('.')[0],
      endTime: endDateTime.split('T')[1].split('.')[0],
      location: eventLocation,
      options: [calendarType],
      timeZone: 'UTC',
    });
  };

  return (
    <div className="confirmation-container">
      <h2>Thank you for your booking, see you soon!</h2>
      <div className="booking-details">
        <h3>{bookingDetails.location}</h3>
        <p>
          {startDate.toLocaleDateString('en-US', {
            weekday: 'long',
            day: 'numeric',
            month: 'long',
          })}
        </p>
        <p>{bookingDetails.time || `${currentDate.getHours()}:${currentDate.getMinutes()}`}</p>
      </div>

      {/* Add to Calendar Dropdown */}
      <button
        className="add-to-calendar"
        onClick={() => setShowCalendarOptions(!showCalendarOptions)}
      >
        Add to calendar
      </button>
      {showCalendarOptions && (
        <div className="calendar-options">
          <div
            className="calendar-option"
            onClick={() => handleAddToCalendar('Apple')}
          >
            <img
              src="https://cdn-icons-png.flaticon.com/512/888/888859.png"
              alt="Apple"
            />
            <span>Apple</span>
          </div>
          <div
            className="calendar-option"
            onClick={() => handleAddToCalendar('Google')}
          >
            <img
              src="https://cdn-icons-png.flaticon.com/512/300/300221.png"
              alt="Google"
            />
            <span>Google</span>
          </div>
          <div
            className="calendar-option"
            onClick={() => handleAddToCalendar('Office365')}
          >
            <img
              src="https://cdn-icons-png.flaticon.com/512/732/732223.png"
              alt="Office365"
            />
            <span>Office365</span>
          </div>
          <div
            className="calendar-option"
            onClick={() => handleAddToCalendar('Outlook.com')}
          >
            <img
              src="https://cdn-icons-png.flaticon.com/512/732/732200.png"
              alt="Outlook.com"
            />
            <span>Outlook.com</span>
          </div>
          <div
            className="calendar-option"
            onClick={() => handleAddToCalendar('Yahoo')}
          >
            <img
              src="https://cdn-icons-png.flaticon.com/512/732/732245.png"
              alt="Yahoo"
            />
            <span>Yahoo</span>
          </div>
        </div>
      )}

      <div className="feedback-section">
        <h3>Please let us know how you heard about us</h3>
        <select className="feedback-dropdown">
          <option value="">Please Select</option>
          <option value="Google Search">Google Search</option>
          <option value="Social Media">Social Media</option>
          <option value="Friend Referral">Friend Referral</option>
          <option value="Other">Other</option>
        </select>
        <button className="feedback-submit" onClick={handleSubmit}>
          Submit
        </button>
      </div>
    </div>
  );
};

export default BookingConfirmation;
