import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/BookTour.css';

const BookTour = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    location: '',
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    desks: '',
    startDate: '',
    interest: '',
    time: '',
  });
  const [selectedMonth, setSelectedMonth] = useState('January');
  const [selectedDate, setSelectedDate] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const nextStep = () => {
    if (step < 2) setStep(step + 1);
  };

  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Combine firstName and lastName into a name field
    const fullName = `${formData.firstName} ${formData.lastName}`.trim();

    if (!fullName || !formData.email || !formData.location || !formData.startDate || !formData.time) {
      alert('Please fill in all required fields.');
      return;
    }

    // Add the name field and navigate to BookingConfirmation
    const bookingDetails = { ...formData, name: fullName };
    console.log('Navigating to BookingConfirmation with:', bookingDetails);
    navigate('/book-confirmation', { state: { bookingDetails } });
  };

  const isWeekend = (day) => {
    const date = new Date(`${selectedMonth} ${day}, ${new Date().getFullYear()}`);
    const dayOfWeek = date.getDay();
    return dayOfWeek === 0 || dayOfWeek === 6; // 0 is Sunday, 6 is Saturday
  };

  return (
    <div className="book-tour">
      <h2>Book a Tour</h2>
      <div className="steps">
        <div className={`step ${step === 1 ? 'active' : ''}`}>
          <span>1</span>
        </div>
        <span className={`step-label ${step === 1 ? 'active' : ''}`}>About You</span>
        <div className={`step ${step === 2 ? 'active' : ''}`}>
          <span>2</span>
        </div>
        <span className={`step-label ${step === 2 ? 'active' : ''}`}>Date & Time</span>
      </div>

      {step === 1 && (
        <form>
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select id="location" name="location" value={formData.location} onChange={handleInputChange} required>
              <option value="">Select location</option>
              <option value="London">London</option>
              <option value="New York">New York</option>
              <option value="Berlin">Berlin</option>
              <option value="Paris">Paris</option>
              <option value="Rome">Rome</option>
            </select>
          </div>

          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input
                type="text"
                name="firstName"
                placeholder="First name"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last name"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            <input
              type="email"
              name="email"
              placeholder="Work email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-buttons">
            <button type="button" onClick={nextStep}>
              Next →
            </button>
          </div>
        </form>
      )}

      {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Pick a date*</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Pick a time*</label>
            <input
              type="time"
              name="time"
              value={formData.time}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-buttons">
            <button type="button" onClick={prevStep}>
              ← Back
            </button>
            <button type="submit">Book Me In!</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
