import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/BookTour.css'; // Custom CSS for styling

const BookTour = () => {
  const [step, setStep] = useState(1); // Track current step (1 or 2)
  const [formData, setFormData] = useState({
    location: '',
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    desks: '',
    interest: '',
    startDate: '',
    time: '',
  });
  const [selectedMonth, setSelectedMonth] = useState('January'); // Current selected month
  const [selectedDate, setSelectedDate] = useState(null); // Current selected date
  const [selectedTime, setSelectedTime] = useState(null); // Current selected time
  const navigate = useNavigate();

  const months = ['January', 'February', 'March']; // Example months
  const dates = [13, 14, 15, 16, 17, 18, 19]; // Example dates
  const timeSlots = [
    '9:00 am', '9:30 am', '10:00 am', '10:30 am',
    '11:00 am', '11:30 am', '12:00 pm', '12:30 pm',
    '1:00 pm', '1:30 pm', '2:00 pm', '2:30 pm',
    '3:00 pm', '3:30 pm', '4:00 pm', '4:30 pm', '5:00 pm',
  ]; // Example time slots

  // Handle input changes in the "About You" form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Move to the next step
  const nextStep = () => {
    if (step < 2) setStep(step + 1);
  };

  // Move to the previous step
  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  // Handle date and time selection
  const handleMonthChange = (month) => {
    setSelectedMonth(month);
    setSelectedDate(null); // Reset selected date
    setSelectedTime(null); // Reset selected time
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedTime(null); // Reset selected time
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  // Submit the form and navigate to the confirmation page
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!selectedDate || !selectedTime) {
      alert('Please select a date and time.');
      return;
    }

    const bookingDetails = {
      ...formData,
      startDate: `${selectedMonth} ${selectedDate}`,
      time: selectedTime,
    };

    navigate('/book-confirmation', { state: { bookingDetails } });
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>

      {/* Step Indicators */}
      <div className="steps">
        <div className={`step ${step === 1 ? 'active' : ''}`}>
          <span>1</span>
          <p>About you</p>
        </div>
        <div className={`step ${step === 2 ? 'active' : ''}`}>
          <span>2</span>
          <p>Date & time</p>
        </div>
      </div>

      {/* Step 1: About You */}
      {step === 1 && (
        <form>
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select
              id="location"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              required
            >
              <option value="">Select location</option>
              <option value="Bermondsey">Bermondsey</option>
              <option value="Soho">Soho</option>
              <option value="Camden">Camden</option>
            </select>
          </div>

          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input
                type="text"
                name="firstName"
                placeholder="First name"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last name"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Work email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="number"
              name="desks"
              placeholder="How many desks do you need?"
              value={formData.desks}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>What are you interested in?*</label>
            <div className="interest-options">
              <button
                type="button"
                className={formData.interest === 'Private Office' ? 'selected' : ''}
                onClick={() => setFormData({ ...formData, interest: 'Private Office' })}
              >
                Private office
              </button>
              <button
                type="button"
                className={formData.interest === 'Coworking' ? 'selected' : ''}
                onClick={() => setFormData({ ...formData, interest: 'Coworking' })}
              >
                Coworking
              </button>
            </div>
          </div>

          <div className="form-buttons">
            <button type="button" onClick={nextStep} className="next-button">
              Agree and proceed →
            </button>
          </div>
        </form>
      )}

      {/* Step 2: Date & Time */}
      {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Pick a date*</label>
            <div className="month-tabs">
              {months.map((month) => (
                <button
                  key={month}
                  className={`month-tab ${selectedMonth === month ? 'active' : ''}`}
                  onClick={() => handleMonthChange(month)}
                >
                  {month}
                </button>
              ))}
            </div>
            <div className="date-selector">
              {dates.map((date) => (
                <button
                  key={date}
                  className={`date-button ${selectedDate === date ? 'active' : ''}`}
                  onClick={() => handleDateChange(date)}
                >
                  {date}
                </button>
              ))}
            </div>
          </div>

          <div className="form-group">
            <label>What time?*</label>
            <div className="time-slots">
              {timeSlots.map((time) => (
                <button
                  key={time}
                  className={`time-slot ${selectedTime === time ? 'active' : ''}`}
                  onClick={() => handleTimeChange(time)}
                >
                  {time}
                </button>
              ))}
            </div>
          </div>

          <div className="guide-info">
            <img
              src="https://via.placeholder.com/50"
              alt="Guide"
            />
            <p>Niamh will be there to meet you</p>
          </div>

          <div className="form-buttons">
            <button type="button" onClick={prevStep}>
              ← Back
            </button>
            <button type="submit" className="submit-button">
              Book me in!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
