import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/BookingConfirmation.css';

const BookingConfirmation = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const bookingDetails = location.state?.bookingDetails;

  if (!bookingDetails) {
    console.error('Booking details are missing. Redirecting...');
    navigate('/book-tour');
    return null;
  }

  const fullName = `${bookingDetails.firstName || ''} ${bookingDetails.lastName || ''}`.trim();

  const handleSubmit = async () => {
    const emailData = {
      name: fullName,
      date: bookingDetails.startDate,
      time: bookingDetails.time,
      location: bookingDetails.location,
      email: bookingDetails.email,
      attendees: bookingDetails.desks,
    };

    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:5000/send-mail', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(emailData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        alert(`Failed to send email confirmation: ${errorData.error}`);
        setIsSubmitting(false);
        return;
      }

      const data = await response.json();
      alert('Email confirmation sent successfully!');
      console.log('Email sent response:', data);
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while sending the email.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="confirmation-container">
      <h2>Thank you for your booking, see you soon!</h2>
      <div className="booking-details">
        <h3>{bookingDetails.location}</h3>
        <p>{new Date(bookingDetails.startDate).toLocaleDateString()}</p>
        <p>{bookingDetails.time}</p>
        <p>For {bookingDetails.desks} people</p>
      </div>
      <button
        className="submit-button"
        onClick={handleSubmit}
        disabled={isSubmitting}
      >
        {isSubmitting ? 'Sending...' : 'Submit'}
      </button>
    </div>
  );
};

export default BookingConfirmation;
