const BookTour = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    location: '',
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    desks: '',
    startDate: '',
    interest: '',
    time: '',
  });

  const [selectedMonth, setSelectedMonth] = useState('January');
  const [selectedDate, setSelectedDate] = useState('');
  const [isPastDate, setIsPastDate] = useState(false);
  const [isToday, setIsToday] = useState(false);
  const navigate = useNavigate();

  // Example of unavailable dates (e.g., January 1st and 25th)
  const unavailableDates = [
    { month: 'January', day: 1 },
    { month: 'January', day: 25 }
  ];

  useEffect(() => {
    const currentDate = new Date();
    const currentYear = currentDate.getFullYear();
    const currentMonth = currentDate.getMonth(); // 0-based index for months (0 = January)
    const currentDay = currentDate.getDate();

    // Set if selected date is in the past
    if (selectedMonth && selectedDate) {
      const selectedDateObj = new Date(currentYear, currentMonth, selectedDate);
      setIsPastDate(selectedDateObj < currentDate); // Compare with current date
    }

    // Check if the selected date is today
    if (selectedDate === currentDay && selectedMonth === currentDate.toLocaleString('default', { month: 'long' })) {
      setIsToday(true);
    } else {
      setIsToday(false);
    }
  }, [selectedDate, selectedMonth]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const nextStep = () => {
    if (step < 2) setStep(step + 1);
  };

  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', { ...formData, selectedMonth, selectedDate });
    navigate('/thank-you');
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>
      <div className="steps">
        <div className={`step ${step === 1 ? 'active' : ''}`}>
          <span>1</span>
        </div>
        <span className={`step-label ${step === 1 ? 'active' : ''}`}>About you</span>

        <div className={`step ${step === 2 ? 'active' : ''}`}>
          <span>2</span>
        </div>
        <span className={`step-label ${step === 2 ? 'active' : ''}`}>Date & time</span>
      </div>

      {step === 1 && (
        <form>
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select
              id="location"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              required
            >
              <option value="">Select location</option>
              <option value="London">London</option>
              <option value="New York">New York</option>
              <option value="Berlin">Berlin</option>
              <option value="Paris">Paris</option>
              <option value="Rome">Rome</option>
            </select>
          </div>

          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input
                type="text"
                name="firstName"
                placeholder="First name"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last name"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Work email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="number"
              name="desks"
              placeholder="How many desks do you need?"
              value={formData.desks}
              onChange={handleInputChange}
              required
            />
            <input
              type="date"
              name="startDate"
              placeholder="When do you want to start?"
              value={formData.startDate}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-buttons">
            <button type="button" onClick={nextStep} className="next-button">
              Agree and proceed →
            </button>
          </div>
        </form>
      )}

      {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="tourBookingForm bookingStep2Formfield">
            <div className="fieldGroupTitle">
              Pick a date: {dataAttributes.dayStartDay} to {dataAttributes.dayEndDay}
            </div>

            {/* Days Selection */}
            <div className="days-picker">
              {[...Array(31)].map((_, index) => {
                const day = index + 1;
                const isUnavailable = unavailableDates.some(
                  (date) => date.month === selectedMonth && date.day === day
                );
                return (
                  <button
                    key={day}
                    type="button"
                    className={`day-button ${
                      selectedDate === day ? 'active' : ''
                    } ${isUnavailable ? 'unavailable' : ''}`}
                    onClick={() => {
                      if (!isUnavailable) setSelectedDate(day);
                    }}
                    disabled={isUnavailable}
                  >
                    {day}
                  </button>
                );
              })}
            </div>

            {/* Error Message for Unavailable or Past Date */}
            {isPastDate && (
              <div className="error-message">
                Sorry! We're not available on this date, please select another date or call our team on 020 8124 3416.
              </div>
            )}

            {/* Message for Today */}
            {isToday && (
              <div className="error-message">
                Sorry! We're not available for bookings today. Please select another date or call our team on 020 8124 3416.
              </div>
            )}

            {/* Time Slots (Only display if the selected date is not today) */}
            {!isToday && selectedDate && (
              <>
                <div className="fieldGroupTitle">What Time?:</div>
                <div className="time-slots">
                  {['09:30 AM', '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM', '12:00 PM', '01:30 PM', '02:00 PM', '02:30 PM', '03:00 PM', '05:00 PM'].map(
                    (time) => (
                      <button
                        key={time}
                        type="button"
                        className={`time-slot ${formData.time === time ? 'active' : ''}`}
                        onClick={() => setFormData({ ...formData, time })}
                      >
                        {time}
                      </button>
                    )
                  )}
                </div>
              </>
            )}
          </div>

          <div className="form-buttons">
            <button type="button" onClick={prevStep} className="prev-button">
              ← Back
            </button>
            <button
              type="submit"
              className="submit-button"
              disabled={!selectedDate || !formData.time}
            >
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
