import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/BookTour.css'; // Custom CSS for styling

const BookTour = () => {
  const [step, setStep] = useState(1); // Tracks the current step
  const [formData, setFormData] = useState({
    location: '',
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    desks: '',
    interest: '',
    startDate: '',
    time: '',
  });
  const [selectedMonth, setSelectedMonth] = useState('January');
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const navigate = useNavigate();

  const months = ['January', 'February', 'March'];
  const dates = Array.from({ length: 31 }, (_, i) => i + 1); // Array from 1 to 31
  const timeSlots = [
    '09:30 AM',
    '10:00 AM',
    '10:30 AM',
    '11:00 AM',
    '11:30 AM',
    '12:00 PM',
    '01:30 PM',
    '02:00 PM',
    '02:30 PM',
    '03:00 PM',
    '05:00 PM',
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const nextStep = () => {
    if (step < 2) setStep(step + 1);
  };

  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleMonthChange = (month) => {
    setSelectedMonth(month);
    setSelectedDate(null);
    setSelectedTime(null);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedTime(null);
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedDate || !selectedTime) {
      alert('Please select a date and time.');
      return;
    }
    const bookingDetails = {
      ...formData,
      startDate: `${selectedMonth} ${selectedDate}`,
      time: selectedTime,
    };
    navigate('/book-confirmation', { state: { bookingDetails } });
  };

  return (
    <div className="book-tour">
      <h2>Book a tour</h2>

      {/* Step Indicators */}
      <div className="steps">
        <div className={`step ${step === 1 ? 'active' : ''}`}>
          <span>1</span>
          <p>About you</p>
        </div>
        <div className={`step ${step === 2 ? 'active' : ''}`}>
          <span>2</span>
          <p>Date & time</p>
        </div>
      </div>

      {/* Step 1: About You */}
      {step === 1 && (
        <form>
          <div className="form-group">
            <label htmlFor="location">Where?*</label>
            <select
              id="location"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              required
            >
              <option value="">Select location</option>
              <option value="Bermondsey">Bermondsey</option>
              <option value="Soho">Soho</option>
              <option value="Camden">Camden</option>
            </select>
          </div>

          <div className="form-group">
            <label>Tell us about you*</label>
            <div className="name-fields">
              <input
                type="text"
                name="firstName"
                placeholder="First name"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last name"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Work email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
            <input
              type="number"
              name="desks"
              placeholder="How many desks do you need?"
              value={formData.desks}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>When do you want to start?*</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleInputChange}
              required
              min={new Date().toISOString().split('T')[0]} // Prevent past dates
            />
          </div>

          <div className="form-group">
            <label>What are you interested in?*</label>
            <div className="interest-options">
              <button
                type="button"
                className={formData.interest === 'Private Office' ? 'selected' : ''}
                onClick={() => setFormData({ ...formData, interest: 'Private Office' })}
              >
                Private office
              </button>
              <button
                type="button"
                className={formData.interest === 'Coworking' ? 'selected' : ''}
                onClick={() => setFormData({ ...formData, interest: 'Coworking' })}
              >
                Coworking
              </button>
            </div>
          </div>

          <div className="form-buttons">
            <button type="button" onClick={nextStep} className="next-button">
              Agree and proceed →
            </button>
          </div>
        </form>
      )}

    {/* Step 2: Date & Time */}
    {step === 2 && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="date-label">
              Pick a date* <span className="date-range">(2025-01-20 to 2025-03-31)</span>
            </label>
            {/* Month Navigation */}
            <div className="date-navigation">
              <button
                onClick={() => handleMonthChange("previous")}
                disabled={currentMonth === 0}
              >
                &lt;
              </button>
              <span>{months[currentMonth]}</span>
              <button
                onClick={() => handleMonthChange("next")}
                disabled={currentMonth === months.length - 1}
              >
                &gt;
              </button>
            </div>
            {/* Date Selector */}
            <div className="date-selector">
              {visibleDates.map((day) => {
                const isWeekend = new Date(2025, currentMonth, day).getDay() % 6 === 0; // Disable weekends
                const today = new Date().setHours(0, 0, 0, 0); // Today's date
                const selectedDateObject = new Date(2025, currentMonth, day).getTime();
                const isPastDate = selectedDateObject < today; // Disable past dates

                return (
                  <button
                    key={day}
                    className={`date-button ${selectedDate === day ? "active" : ""}`}
                    disabled={isWeekend || isPastDate}
                    onClick={() => handleDateChange(day)}
                  >
                    {day}
                  </button>
                );
              })}
            </div>
          </div>

          {/* Time Slots */}
          <div className="form-group">
            <label className="time-label">What time?*</label>
            {selectedDate && (
              <div className="time-slots">
                {timeSlots.map((time) => (
                  <button
                    key={time}
                    className={`time-slot ${selectedTime === time ? "active" : ""}`}
                    onClick={() => handleTimeChange(time)}
                  >
                    {time}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="guide-info">
            <img
              src="https://via.placeholder.com/50"
              alt="Niamh"
              className="guide-image"
            />
            <p>Niamh will be there to meet you</p>
          </div>

          {/* Buttons */}
          <div className="form-buttons">
            <button type="button" onClick={() => setStep(1)} className="back-button">
              ← Back
            </button>
            <button type="submit" className="submit-button">
              Book Me In!
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default BookTour;
